mapper.select:
select count(id)
from t_user

    
        
            and id = #{user.id}
        
        
            and email = #{user.email}
        
        
            and username = #{user.username}
        
        
            and real_name = #{user.realName}
        
        
            and password = #{user.password}
        
        
            and sex = #{sex}
        
        
            and lang_key = #{user.langKey}
        
        
            and token = #{user.token}

select

from t_user

    
        
            and id = #{user.id}
        
        
            and email = #{user.email}
        
        
            and username = #{user.username}
        
        
            and real_name = #{user.realName}
        
        
            and password = #{user.password}
        
        
            and sex = #{sex}
        
        
            and lang_key = #{user.langKey}
        
        
            and token = #{user.token}
        
    

limit 1

select

from t_user

    
        
            and id = #{user.id}
        
        
            and email = #{user.email}
        
        
            and username = #{user.username}
        
        
            and real_name = #{user.realName}
        
        
            and password = #{user.password}
        
        
            and sex = #{sex}
        
        
            and lang_key = #{user.langKey}
        
        
            and token = #{user.token}
        
    

order by register_date asc
